  const validate = values => {
    errors = {}

    if(formik.values.emailField === "") {
      errors.emailField = "emailError"
      console.log('nothing')
    }
    if(formik.values.pswField === "") {
      errors.pswField = "pswError"
    }
    return errors
  }

  const formik = useFormik({
    initialValues: {
      email: '',
      password: '',
    },
    validate,

    onSubmit: values => {
      alert(JSON.stringify(values, null, 2))
  }
  });

  return (
    <div>

<form onSubmit={formik.handleSubmit}>

